extension String {
    func randomName() -> String {
        if String.arrayRandomName.isEmpty {
            String.arrayRandomName = String.namesArray
        }
        
        let name = String.arrayRandomName.removeRandom() ?? "Бран"
        return name
    }
    
    func randomSurname() -> String {
        if String.arrayRandomSurname.isEmpty {
            String.arrayRandomSurname = String.surnamesArray
        }
        
        let name = String.arrayRandomSurname.removeRandom() ?? "Старк"
        return name
    }
    
    func randomTagname() -> String {
        if String.arrayRandomTagname.isEmpty {
            String.arrayRandomTagname = String.tagnamesArray
        }
        
        let name = String.arrayRandomTagname.removeRandom() ?? "имя тег"
        return name
    }
    
    func randomAboutMe() -> String {
        if String.arrayRandomAboutMe.isEmpty {
            String.arrayRandomAboutMe = String.aboutMeArray
        }
        
        let name = String.arrayRandomAboutMe.removeRandom() ?? "обо мне"
        return name
    }
    
    func randomTitleBlog() -> String {
        if String.arrrayRandomTitleBlog.isEmpty {
            String.arrrayRandomTitleBlog = String.titlesBlogArray
        }
        
        let name = String.arrrayRandomTitleBlog.removeRandom() ?? "Заголовок"
        return name
    }
    
    func randomTextBlog() -> String {
        if String.arrayRandomTextBlog.isEmpty {
            String.arrayRandomTextBlog = String.textsBlogArray
        }
        
        let name = String.arrayRandomTextBlog.removeRandom() ?? "текст"
        return name
    }
    
    func randomTagsBlog() -> [String] {
        var array: [String] = []
        let max = String.arrayRandomTagsBlog.count < 10 ? String.arrayRandomTagsBlog.count : 10
        String.arrayRandomTagsBlog = String.tagsBlogArray
        
        for _ in 1...Int.random(in: 1...max) {
            array.append(String.arrayRandomTagsBlog.removeRandom() ?? "тег")
        }
        
        return array
    }
    
    func condenseWhitespace() -> String {
        let components = self.components(separatedBy: .whitespacesAndNewlines)
        return components.filter { !$0.isEmpty }.joined(separator: " ")
    }
    
